-- ========================================
-- VERIFICATION QUERIES
-- ========================================

-- Check that profiles were created
SELECT username, name, subscription_plan, created_at FROM profiles WHERE email LIKE '%demo@promptsgo.com%';

-- Check that prompts were created
SELECT title, category, hearts, save_count, created_at FROM prompts WHERE user_id IN (
  '11111111-1111-1111-1111-111111111111'::uuid,
  '22222222-2222-2222-2222-222222222222'::uuid,
  '33333333-3333-3333-3333-333333333333'::uuid,
  '44444444-4444-4444-4444-444444444444'::uuid,
  '55555555-5555-5555-5555-555555555555'::uuid
) ORDER BY hearts DESC;

-- Summary stats
SELECT 
  COUNT(*) as total_prompts,
  SUM(hearts) as total_hearts,
  SUM(save_count) as total_saves,
  AVG(hearts)::int as avg_hearts
FROM prompts 
WHERE user_id IN (
  '11111111-1111-1111-1111-111111111111'::uuid,
  '22222222-2222-2222-2222-222222222222'::uuid,
  '33333333-3333-3333-3333-333333333333'::uuid,
  '44444444-4444-4444-4444-444444444444'::uuid,
  '55555555-5555-5555-5555-555555555555'::uuid
);